import React, { useEffect, useState } from 'react';

import { GoogleLogin } from 'react-google-login';
import { gapi } from 'gapi-script';
import { connect } from 'react-redux';

import { signIn, googleId, mail } from '../actions';

const Header = (props, { signIn, googleId, mail }) => {
  console.log('props:', props.authentication);
  const clientId =
    '196808343155-ghqras7mpi9t9mrj4rrh8fopluu5ovij.apps.googleusercontent.com';
  const [profile, setProfile] = useState([]);
  const [loginstatus, setloginstatus] = useState(false);
  useEffect(() => {
    const initClient = () => {
      gapi.client.init({
        clientId: clientId,
        scope: '',
      });
    };
    gapi.load('client:auth2', initClient);
  });
  const onSuccess = (res) => {
    console.log('success:', res);
    setProfile(res.profileObj);
    setloginstatus(true);
    signIn(true);
    googleId(res.profileObj.googleId);
  };
  const onFailure = (err) => {
    console.log('failed:', err);
  };
  const logOut = () => {
    setProfile(null);
    setloginstatus(false);
  };
  return (
    <div className="container">
      <h2>React Google Login</h2>
      <br />
      <GoogleLogin
        clientId={clientId}
        buttonText="Sign in with Google"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={'single_host_origin'}
        isSignedIn={() => setloginstatus(true)}
      />
    </div>
  );
};
const mapsToProps = (state) => {
  return state;
};
export default connect(mapsToProps, { signIn, googleId, mail })(Header);
